import React, { useState } from 'react';
import { Camera, Upload, X } from 'lucide-react';
import { savePhoto } from '../utils/localStorage';
import './PhotoUpload.css';

const PhotoUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    setIsUploading(true);
    // Simulate upload process
    setTimeout(() => {
      // Save photo to localStorage
      if (savePhoto(preview)) {
        alert('Photo uploaded successfully!');
        setSelectedFile(null);
        setPreview(null);
      } else {
        alert('Error saving photo. Please try again.');
      }
      setIsUploading(false);
    }, 2000);
  };

  const handleRemove = () => {
    setSelectedFile(null);
    setPreview(null);
  };

  return (
    <div className="photo-upload">
      <div className="container">
        <div className="page-header">
          <h1>Photo Upload</h1>
          <p>Take a photo of your meal to automatically track your nutrition</p>
        </div>

        <div className="upload-section">
          <div className="upload-card">
            {!preview ? (
              <div className="upload-area">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileSelect}
                  className="file-input"
                  id="file-input"
                />
                <label htmlFor="file-input" className="upload-label">
                  <Camera size={48} />
                  <h3>Upload Food Photo</h3>
                  <p>Click to select or drag and drop an image</p>
                </label>
              </div>
            ) : (
              <div className="preview-area">
                <div className="preview-header">
                  <h3>Photo Preview</h3>
                  <button onClick={handleRemove} className="remove-btn">
                    <X size={20} />
                  </button>
                </div>
                <img src={preview} alt="Preview" className="preview-image" />
                <div className="preview-actions">
                  <button
                    onClick={handleUpload}
                    disabled={isUploading}
                    className="btn btn-primary"
                  >
                    {isUploading ? 'Uploading...' : 'Upload Photo'}
                  </button>
                </div>
              </div>
            )}
          </div>

          <div className="upload-tips">
            <h3>Tips for Better Results</h3>
            <ul>
              <li>Take photos in good lighting</li>
              <li>Include the entire meal in the frame</li>
              <li>Make sure the food is clearly visible</li>
              <li>Avoid blurry or dark images</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PhotoUpload;
